(2, 'KM01', 'KM-first', 'Chúc may mắn', 100000, 'Hoa hồng trắng Shizuku Rose, Hoa hồng đỏ Monalisa Rose, Hoa hồng Miyakox, Hoa Cúc Anh', 'Sinh nhật, tốt nghiệp', 1, '../img/home/HoaKM/KM01.jpg', '<p>l&agrave; sự kết hợp h&agrave;i h&ograve;a giữa những b&ocirc;ng hoa tươi đẹp nhất, được nhập từ khu vực đ&agrave; lạt, c&ugrave;ng những b&ocirc;ng baby xinh tươi được nhập từ v&ugrave;ng H&agrave; Lan. Để g&oacute;p phần tạo n&ecirc;n một n&eacute;t đẹp độc đ&aacute;o. Kh&ocirc;ng chỉ đẹp m&agrave; c&ograve;n mang nhiều &yacute; nghĩa s&acirc;u sắc để gửi tới người nhận.</p>', NULL, '2021-12-30 22:32:32'),
(3, 'KM02', 'KM', 'Chúc mừng\r\n', 300000, 'Hoa hồng đỏ Red Eden Rose, Các Tường, Hoa Ly Hồng, Cẩm tú cầu xanh\r\n', 'Sinh nhật, tốt nghiệp\r\n', 1, '../img/home/HoaKM/KM02.jpg ', 'Giỏ hoa là sự kết hợp giữa Hoa hồng đỏ Red Eden Rose, Các Tường, Hoa Ly Hồng, Cẩm tú cầu xanh như một bài ca chúc mừng mà bạn muốn nhắn gởi tới người nhận. Giỏ hoa tạo nên nét sang trọng như một lời chúc mừng, chúc sức khoẻ dành cho đồng nghiệp, đối tác trong lần đầu gặp mặt hay trong những ngày đặc biệt nào đó như sinh nhật, ngày quốc tế phụ nữ 8/3 hay 20/10…', NULL, NULL),
(4, 'KM03', 'KM', 'Ngày nắng\r\n', 500000, 'Hoa hồng vàng Golden Celebration rose, Cúc chi vàng, Hoa Baby, Lan Vũ Nữ\r\n', 'Sinh nhật, tốt nghiệp\r\n', 1, '../img/home/HoaKM/KM03.jpg ', 'Tượng trưng cho tình bạn chân thành và ấm áp. Gửi tặng một bó hoa hồng vàng cho những người bạn thân sẽ giúp bạn thể hiện tình cảm, sự quan tâm và gợi nhắc lại những kỷ niệm tình bạn giữa 2 người, cũng là biểu tượng của sự thành công, sự ghi nhận những đóng góp và nỗ lực trong công việc. Vì vậy, giỏ hoa Ngày nắng cũng được sử dụng để gửi lời chúc mừng tới bạn bè, người thân hoặc đối tác trong ngày khai trương.', NULL, NULL),
(5, 'KM04', 'KM', 'Bầu trời\r\n', 150000, 'Hoa hồng xanh, Hoa Baby\r\n', 'Sinh nhật, tốt nghiệp\r\n', 1, '../img/home/HoaKM/KM04.jpg ', 'Thể hiện tình yêu bất tử, tình yêu vĩnh cửu. Ngoài việc dành tặng bạn gái/người yêu, thì màu hoa này cũng thích hợp để dành tặng nam giới, một màu xanh tươi, màu của hy vọng, màu của thiên nhiên đất trời. Hơn nữa, hoa hồng xanh còn tượng trưng cho một cái gì đó không thể thực hiện được, một cái gì đó bí ẩn.', NULL, '2021-12-30 19:27:28'),
(6, 'KM05', 'KM', 'Vươn lên\r\n', 250000, 'Lan Vanda đỏ, Hoa hướng dương, Hoa cẩm chướng;\r\n', 'Sinh nhật, tốt nghiệp\r\n', 1, '../img/home/HoaKM/KM05.jpg ', 'Giỏ hoa khoác lên mình một màu vàng vô cùng tỏa sáng, rực rỡ như những động lực mạnh mẽ, sự quyết tâm. Với giỏ “ Vươn lên” mong muốn mọi người hãy cũng nhau cố gắng vượt qua mọi khó khăn và ở cuối đoạn đường ánh sáng, niềm tin luôn luôn ở đó đợi chúng ta.', NULL, NULL),
(7, 'KM06', 'KM', 'Hồ điệp trắng\r\n', 1000000, ' Lan hồ điệp\r\n', '20/11\r\n', 1, '../img/home/HoaKM/KM06.jpg ', 'Chậu lan hồ điệp trắng biểu tượng cho sự khiêm nhường, tinh khiết. Màu trắng biểu thị cho sự giàu sang phú quý. Hoa lan trắng chính là đại diện của sự ngây thơ, xinh đẹp và trang trọng. Nó là bắt đầu cho một giai đoạn, một thời kỳ mới. Gửi tặng hoa màu trắng cho thấy ở bạn có sự chân thành và tràn đầy hy vọng, tình yêu, mong được cùng nhau bắt đầu một hành trình mới, bỏ lại quá khứ phía sau.', NULL, NULL),
(8, 'KM07', 'KM', 'Hồ điệp tím\r\n', 1500000, 'Lan hồ điệp tím\r\n', '20/11\r\n', 0, '../img/home/HoaKM/KM07.jpg ', 'Chậu lan hồ điệp trắng biểu tượng cho sự khiêm nhường, tinh khiết. Màu trắng biểu thị cho sự giàu sang phú quý. Hoa lan trắng chính là đại diện của sự ngây thơ, xinh đẹp và trang trọng. Nó là bắt đầu cho một giai đoạn, một thời kỳ mới. Gửi tặng hoa màu trắng cho thấy ở bạn có sự chân thành và tràn đầy hy vọng, tình yêu, mong được cùng nhau bắt đầu một hành trình mới, bỏ lại quá khứ phía sau.', NULL, NULL),
(9, 'KM08', 'KM', 'Bạch liên \r\n', 180000, ' Hoa sen trắng\r\n', 'Lễ chùa, ...\r\n', 1, '../img/home/HoaKM/KM08.jpg ', 'Hoa sen trắng trong phong thủy còn được thể hiện cho sự thanh cao, thoát tục và cả sự hoàn mỹ. Hình ảnh hoa sen trắng đẹp trong phong thủy giúp điều hòa khí vượng, tăng cường năng lượng tốt và ngăn chặn cả những điều xấu giúp gia chủ tránh ưu phiền, tĩnh tâm.', NULL, NULL),
(10, 'KM09', 'KM', 'Nắng mai\r\n', 200000, 'Hoa hướng dương\r\n', 'Sinh nhật, tốt nghiệp\r\n', 0, '../img/home/HoaKM/KM09.jpg ', 'Hoa hướng dương tượng trưng cho sự chung thủy, quân trung, hiếu nghĩa vì hoa hướng dương luôn hướng thẳng về phía mặt trời. Chính vì thế nó mang tới cho căn nhà một màu vàng ấm áp, nguồn năng lượng tốt đẹp, thắp sáng những nơi u tối, tạo cảm giác ấm áp và không khí tươi vui tràn đầy sức sống. Với những ý nghĩa đó, hộp hoa Nắng mai với hoa chủ đạo màu vàng sẽ là món quà tuyệt vời cho những người thân yêu của bạn.', NULL, NULL),
(11, 'KM10', 'KM', 'Cúc Tana\r\n', 350000, 'Hoa cúc tana\r\n', 'Sinh nhật, tốt nghiệp\r\n', 1, '../img/home/HoaKM/KM10.jpg ', 'Chính vì đặc điểm của từng bông cúc là lớp cánh trắng nhỏ khắng khít ôm lấy nhau mà trong tình bạn, cúc Tana đại diện cho sự gắn kết, một tình bạn bền lâu và đầy chân thành. Trong tình yêu, ý nghĩa của cúc Tana đẹp tựa như mối tình đầu, mối tình mới chớm nở đầy thuần khiết và rất khó phai.', NULL, NULL),
(12, 'HT01', 'HT-first', 'Cúc mẫu đơn', 500000, 'Hoa cúc mẫu đơn', 'Lễ chùa, thăm hỏi', 1, '../img/home/HoaTuoi/HT01.jpg', 'Hoa cúc mẫu đơn là loài hoa đặc trưng của mùa hè vui tươi, tràn đầy sức sống và năng lượng nên chúng mang lời nhắn “ Luôn luôn hạnh phúc và yêu đời\". Là biểu tượng cho niềm vui, hạnh phúc, tình yêu và trường tồn vĩnh cửu.', NULL, NULL),
(13, 'HT02', 'HT', 'Cúc Cali', 480000, 'Hoa cúc Cali', 'Thăm hỏi, hẹn hò, sinh nhật', 0, '../img/home/HoaTuoi/HT02.jpg', 'Tượng trưng cho sự hy vọng vào những điều tốt đẹp, là một nguồn năng lượng dồi dào cho ngày mới.', NULL, NULL),
(14, 'HT03', 'HT', 'Bình tầm xuân vàng', 2900000, 'Hoa nụ tầm xuân', 'Thăm hỏi, Tết', 1, '../img/home/HoaTuoi/HT03.jpg', 'Màu vàng của những cành nụ tầm xuân không chỉ tượng trưng cho sự sang trọng, quý phái mà còn mang ý nghĩa đem thịnh vượng đến cho gia đình. Vẻ ngoài của sắc vàng mang đến sự hứa hẹn của một tương lại tốt đẹp, đẩy cao tinh thần lạc quan, vui vẻ, thịnh vượng. Vì thế, chưng bình hoa Tầm xuân vàng khoe sắc là cách đón may mắn, đón niềm vui và sự sung túc trong năm mới.', NULL, NULL),
(15, 'HT04', 'HT', 'Lọ hoa mai, nụ tầm xuân', 190000, 'Hoa mai, hoa nụ tầm xuân', 'Thăm hỏi, Tết', 1, '../img/home/HoaTuoi/HT004.jpg', 'Nụ tầm xuân màu đỏ tượng trưng cho mọi sự may mắn, thuận lợi và hanh thông, thường được nhiều người lựa chọn để trang trí trong nhà với mong muốn đem đến nhiều may mắn và thuận buồm xuôi gió dành cho mọi thành viên trong gia đình.Hoa mai ví như là biểu tượng của cốt cách, luôn giữ vững trong tâm trí đạo lý ân nghĩa, như sức sống bền bỉ trải qua bao gió sương để rồi đơm hoa đúng vào đầu xuân mang đến sắc hương ngọt ngào. Bên cạnh đó sắc mai vàng tượng trưng cho sự sung túc, giàu sang phú quý. Mai', NULL, NULL),
(16, 'HT05', 'HT', 'Cúc đồng tiền', 300000, 'Hoa cúc đồng tiền', 'Tiệc, thăm hỏi', 0, '../img/home/HoaTuoi/HT05.jpg', 'Hoa đồng tiền với những màu sắc và vẻ ngoài tươi sáng rực rỡ luôn mang lại những cảm xúc và dấu ấn khó phai cho người nhận. Loài hoa ấy không chỉ thể hiện sự hạnh phúc, mang đến sự vui vẻ mà còn thể hiện sự ngây thơ và lòng ca ngợi.', NULL, NULL),
(17, 'HT06', 'HT', 'Bụi phấn ngọt ngào', 1200000, 'Hoa hướng dương, hoa hồng', 'Cảm ơn', 0, '../img/home/HoaTuoi/HT06.jpg', 'Hoa hồng phấn tượng trưng cho tình yêu đầu tiên, cảm động, tình yêu vĩnh cửu và tình yêu ngọt ngào. Màu phấn luôn là màu đại diện của con gái và phù hợp với mối tình đầu của con gái. Nếu bạn tỏ tình với một cô gái thì hoa hồng phấn là sự lựa chọn tốt nhất.', NULL, NULL),
(18, 'HT07', 'HT', 'Hoa cưới dạng tròn Bledermeier', 900000, 'Hoa hồng, hoa đồng tiền', 'Cưới hỏi', 1, '../img/home/HoaTuoi/HT07.jpg', 'Với dáng hoa gọn nhẹ, nhe nhàng hoa cưới dạng tròn mang đến cho cô dâu một vẻ đẹp tươi mới và trẻ trung. Dù xuất hiện từ lâu nhưng dạng hoa cưới tròn vẫn luôn được nhiều cô dâu ưa thích bởi sự đơn giản và xinh xắn của nó.', NULL, NULL),
(19, 'HT08', 'HT', 'Set hoa cẩm tú cầu', 200000, 'Hoa cẩm tú cầu', 'Xin lỗi, biết ơn', 1, '../img/home/HoaTuoi/HT08.jpg', 'Cẩm tú cầu là tượng trưng cho sự lạnh lùng, vô cảm. ... Nên từ đó về sau, cẩm tú cầu cũng là loài hoa gửi gắm những lời xin lỗi. Và, từ vẻ bề ngoài với những cánh hoa mỏng manh, chen chúc nhau tạo thành một quả cầu hoa, nên cẩm tú cầu còn tượng trưng cho sự biết ơn và chân thành.', NULL, NULL),
(20, 'HT09', 'HT', 'Hoa ly', 100000, 'Hoa ly', 'Thăm hỏi, Tết', 1, '../img/home/HoaTuoi/HT09.jpg', 'Hoa Ly từ lâu đã được mệnh danh là một loài hoa thanh cao và quý phái, nó không những tượng trưng cho sắc đẹp, đức hạnh mà còn là sự kiêu hãnh và cả tình yêu cao thượng, chung thủy. Chính bởi vậy, hoa Ly không chỉ thích hợp để dành tặng mẹ, người yêu mà còn rất thích hợp cho ngày chúc mừng, tốt nghiệp, khai trương...', NULL, NULL),
(21, 'HT10', 'HT', 'Hoa lưu ly', 450000, 'Hoa lưu ly', 'Thăm hỏi, cưới hỏi', 1, '../img/home/HoaTuoi/HT10.jpg', 'Một trong những ý nghĩa thông điệp đầu tiên mà người ta thường hay nghĩ tới mỗi khi nhắc đến hoa lưu ly đó là Forget Me Not – “Xin đừng quên tôi” . Không chỉ dừng lại ở lời nhắn nhủ đó, ý nghĩa của hoa lưu ly còn là biểu tượng cho một tình yêu thủy chung và bất diệt.', NULL, NULL),
(22, 'GS01', 'GS-first', 'Bó hoa hồng đỏ ánh sao đêm', 390000, 'Hoa hồng đỏ, hoa salem trắng', 'Giáng Sinh', 1, '../img/home/HoaGS/GS01.jpg', 'Hoa hồng đỏ dùng để bày tỏ thông điệp rằng \"anh yêu em\". Hoa hồng đỏ gắn liền với truyền thuyết vị nữ thần tình yêu, chính vì thế nên loài hoa này được xem như một biểu tượng của tình yêu nồng nàn, ngọt ngào và mãnh liệt với nhiều cung bậc cảm xúc khác nhau. Ngoài ra hoa hồng đỏ còn mang ý nghĩa về sự hoàn mỹ', NULL, NULL),
(23, 'GS02', 'GS', 'Hoa Giáng Sinh', 700000, 'Hoa hồng đỏ, lá thông', 'Giáng Sinh', 0, '../img/home/HoaGS/GS02.jpg', 'Những bông hồng đỏ xen lẫn giữa màu xanh của lá cây như n hững đóm lửa nhỏ tạo nên sự ấm áp mà tinh tế. Màu xanh lá cây tươngj trưng cho sự tươi mới kết hợp cùng với màu đỏ của nhiệt huyết, mang ý nghĩa chúc cho người nhận đuọc hoa này luôn vui tươi và tràn đầy sức sống.', NULL, NULL),
(24, 'GS03', 'GS', 'Bó hoa hạnh phúc', 900000, 'Hoa hồng đỏ, hoa lan hồ điệp', 'Giáng Sinh', 1, '../img/home/HoaGS/GS03.jpg', 'Màu đỏ của sự năng động, màu xanh của sự tươi mới ! Này cậu ơi! Tớ muốn nói là cậu trong rất trẻ trung và năng động á, tràn đầy sức sống. Bên cạnh đó cậu cũng là một con người hồn nhiên, tràn đầy tình yêu và hy vọng, giống với những cánh lan Hồ Điệp này vậy.', NULL, NULL),
(25, 'GS04', 'GS', 'Cây Giáng Sinh', 845000, 'Hoa hồng đỏ, hoa cúc Cali,...', 'Giáng Sinh', 1, '../img/home/HoaGS/GS004.jpg', 'Tớ tin cậu ! Bó hoa muốn gửi đến người nhận một thông điệp này thôi. Giữ sự bao trùm của màu xanh, trắng thì cậu chính là những đó hồng đó. Tuy nhìn có vẻ lẽ loi giữ sự bao trùm của màu xanh. Nhưng tớ tin cậu sẽ luôn nổi bật giống như những đóa hồng này.', NULL, NULL),
(26, 'GS05', 'GS', 'Cây thông mini', 200000, 'Hoa hồng trắng, lá thông, các phụ kiện,...', 'Giáng Sinh', 0, '../img/home/HoaGS/GS05.jpg', 'Mùa đông này lạnh lắm . . . ! Một màu trắng chủ đạo xuyên suốt tạo cảm giác cô đơn và có chút se lạnh khi nhìn vào. Bó hao này muốn nhắn với người nhận rằng: \"Mùa đông này lạnh lắm. Cậu nên tìm một người ở bân cạnh mình đi. . .Chẳng hạn như tớ !\" ', NULL, NULL),
(27, 'GS06', 'GS', 'Bó hoa hồng đỏ ánh sao đêm', 450000, 'Hoa hồng đỏ, hoa salem trắng', 'Giáng Sinh', 1, '../img/home/HoaGS/GS06.jpg', 'Mùa đông đến rồi, Giáng sinh vui vẻ.', NULL, NULL),
(28, 'GS07', 'GS', 'Cay thông NOEL', 600000, 'Hoa hồng đỏ, lá thông, các phụ kiện,...', 'Giáng Sinh', 1, '../img/home/HoaGS/GS07.jpg', 'Tớ tin cậu ! Bó hoa muốn gửi đến người nhận một thông điệp này thôi. Giữ sự bao trùm của màu xanh, trắng thì cậu chính là những đó hồng đó. Tuy nhìn có vẻ lẽ loi giữ sự bao trùm của màu xanh. Nhưng tớ tin cậu sẽ luôn nổi bật giống như những đóa hồng này.', NULL, NULL),
(29, 'GS08', 'GS', 'Vòng nguyệt quế Giáng Sinh', 600000, 'Lá thông, quả thông, các phụ kiện,...', 'Giáng Sinh', 0, '../img/home/HoaGS/GS08.jpg', 'Vòng nguyệt quế, hay còn gọi là vòng ô rô, được biết đến với mục đích phổ biến nhất là trang trí nhà cửa, đặc biệt vào dịp Giáng sinh. Song, ít người biết rằng vòng nguyệt quế còn là một biểu tượng trong nghi thức chào đón Giáng sinh tại các nhà thờ và gia đình theo đạo. Nghi thức này có nguồn gốc từ Đức. Theo đó, vòng nguyệt quế được đặt nằm với 4 cây nến trắng dựng bên trên. Vào mỗi Chủ nhật kể từ tuần thứ 4 trước Giáng sinh, người ta sẽ thắp một cây nến trên vòng nguyệt quế.', NULL, NULL),
(30, 'GS09', 'GS', 'Cây thông mini', 200000, 'Lá thông, các phụ kiện...', 'Giáng Sinh', 0, '../img/home/HoaGS/GS09.jpg', 'Chúc cậu Giáng Sinh vui vẻ.', NULL, NULL),
(31, 'GS10', 'GS', 'Hoa Giáng Sinh', 400000, 'Hoa hồng vàng, lá thông, quả thông, các phụ kiện,...', 'Giáng Sinh', 0, '../img/home/HoaGS/GS10.jpg', 'Giáng sinh an lành.', NULL, NULL),
(32, 'TA01', 'TA-first', 'Bó hoa tri ân', 780000, 'Hoa hồng trắng, hoa hồng vàng, hoa cúc, hoa thọ', 'Tri ân', 1, '../img/home/HoaTriAn/TA01.jpg', 'Thể hiện lên tình cảm chân thành của bạn giành cho thầy cô, bậc sinh thành…những người đã sinh ra, dạy dỗ và dìu dắt bạn để bạn có thể trưởng thành như ngày hôm nay. Tone màu vàng hồng chính là một tone màu của ánh dương, của rực rỡ. Nó tỏa sáng như ánh sáng mặt trời đã chiếu sáng, soi rọi sưởi ấm và dõi theo từng bước đi của bạn. Chính màu vàng hồng đó là màu của sự tri ân, cảm ơn sâu sắc đến người thấy kính mến của bạn.', NULL, NULL),
(33, 'TA02', 'TA', 'Tạ ơn', 1790000, 'Hoa hồng trắng, hoa hồng tím, hoa lan hồ điệp', 'Tri ân', 1, '../img/home/HoaTriAn/TA02.jpg', 'Đối với người yêu của mình trong ngày lễ tạ ơn thì có thể dành tặng cho họ bó hoa vừa thể hiện được sự trân trọng trong con người của mình, vừa thể hiện được sự trân trọng trong tình cảm của  2 người với nhau. Hi vọng là với những điều này thì 2 người ngày càng hiểu nhau và quan tâm tới nhau nhiều hơn nữa.', NULL, NULL),
(34, 'TA03', 'TA', 'Nắng ban mai', 2000000, 'Hoa hồng trắng, hoa hồng vàng, hoa cúc, lan hồ điệp,...', 'Tri ân', 0, '../img/home/HoaTriAn/TA03.jpg', ' Màu vàng của những đóa hoa hướng dương là màu của ánh ban mai, ấm áp và chan hòa. Nó mang ý nghĩa của sự sống, của nghị lực vươn cao, của niềm đam mê cháy mãi. Còn cẩm chướng là biểu tượng của sự may mắn, tình yêu, sự chân thành và hạnh phúc. Điểm tô thêm salem trắng thanh nhẹ và thuần khiết, lan vũ nữ mang âm hưởng của sự say mê và cuồng nhiệt... ', NULL, NULL),
(35, 'TA04', 'TA', 'Chân thành cảm ơn', 1050000, 'Hoa hướng dương, hoa cúc Calimero,...', 'Tri ân, 20/11 ', 1, '../img/home/HoaTriAn/TA04.jpg', 'Hoa Hướng Dương đại diện cho sự hy sinh thầm lặng. Kết hợp cùng những bông cúc Calimero nhỏ nhắn đại diện cho sự cố gắn và tình yêu. Cả bó hoa mang thông điệp: Cảm ơnvif tất cả, tôi rất biết ơn. Bó hoa này thíc hợp nhất để tặng cho người thầy (cô) mà bạn kính trọng để nói lòng biết ơn cũng như sự hy sinh thầm lặng và tình yêu mà họ đã dành cho bạn. ', NULL, NULL),
(36, 'TA05', 'TA', 'Điều muốn nói', 900000, 'Hoa hồng phấn', 'Tri ân', 1, '../img/home/HoaTriAn/TA05.jpg', 'Tôi có rất nhiều điều muốn nói . . . nhưng lại không cách nào để diễn đạt. Cảm ơn vì tất cả.', NULL, NULL),
(37, 'TA06', 'TA', 'Tỏa nắng', 670000, 'Hoa hướng dương', 'Tri ân,20/11', 0, '../img/home/HoaTriAn/TA06.jpg', 'Sự hy sinh của bạ tôi đều cảm nhận được. Cảm ơn! Trong mắt tôi bạn luôn là một người \"tỏa sáng\" ', NULL, NULL),
(38, 'TA07', 'TA', 'Mỉm cười', 750000, 'Hoa hồng trắng, hoa hướng dương, lan hồ điệp', 'Tri ân', 1, '../img/home/HoaTriAn/TA07.jpg', 'Bạn là mọt người luôn mỉm cười, cho dù tôi có làm gì đi nữa, có làm sai hay thất bại. Bất cứ lúc nào đối diện với tôi bạn luôn nở một nụ cười, lúc nào cũng ửng họo và động viên tôi. Cảm ơn bạn, bạn luôn là chỗ dự tinh thần của tôi. Mong bạn luôn vui vả tràng đầy năng lượng như những hóa lan nhỏ này, tuy nhỏ bé nhưng rực sáng. Luôn trẻ trung và giàu năng lượng.', NULL, NULL),
(39, 'TA08', 'TA', 'Đợi ai đó', 800000, 'Hoa hướng dương, hoa hồng vàng', 'Tri ân', 0, '../img/home/HoaTriAn/TA08.jpg', 'Màu vàng Hoa hồng là màu của sự tươi sáng, hạnh phúc, ấm áp, là loài hoa biểu tượng cho tình bạn chân thành, không vụ lợi. Đây còn là lời chúc may mắn, tốt lành tới những người thương yêu nhất của bạn. Ngoài ra, hoa hồng màu vàng còn đặc biệt dành cho những người kính yêu thể hiện lòng biết ơn, tôn kính. Kết hợp cùng \"sự hy sinh thầm lặng\" của hoa hướng dương. Tôi muôn nói rằng: \"Cảm ơn vì tất cả !\" ', NULL, NULL),
(40, 'TA09', 'TA', 'Biết ơn', 950000, 'Hoa ly, hoa Hồng phấn, Hồng Môn xanh', 'Tri ân', 1, '../img/home/HoaTriAn/TA09.jpg', 'Hồng môn xanh là biểu trưng cho sức sống mạnh mẽ và luồng sinh khí mới đem lại sự may mắn, những điều tốt lành cho chủ nhân. Kết hợp cùng hoa Ly vàng đâij biểu cho sự biết ơn. Là một đóa hoa đầy ý nghĩa dàng tặng cho thầy cô nhân ngày lễ', NULL, NULL),
